import { Component, Input } from '@angular/core';
import { NgbActiveModal } from '@ng-bootstrap/ng-bootstrap';
import { Client } from '../client-main/client';
import { ClientService } from '../client-main/client.service';

@Component({
  selector: 'app-client-form',
  templateUrl: './client-form.component.html',
  styleUrls: ['./client-form.component.css']
})
export class ClientFormComponent {

  @Input() client: Client;

  constructor(public modal: NgbActiveModal) { }

  saveClient() {
    // Aquí puedes validar los datos ingresados antes de guardar
    // Por ejemplo, si sharedKey está vacío, muestra el mensaje de error
    if (!this.sharedKey) {
      this.sharedKeyError = true;
      return;
    }

    // Si todo está correcto, puedes cerrar el modal y enviar los datos al backend
    this.modal.close(/* datos del nuevo cliente */);
  }
}
